nodecount = 0

def assignEdgeWeightsIfMissing(treenode):
    global nodecount
    treenode['node_data']['nodeid'] = nodecount
    if ('edge_data' not in treenode):
        treenode['edge_data'] = {}
    if not ('weight' in treenode['edge_data']):
        treenode['edge_data']['weight'] = 1.0
    elif treenode['edge_data']['weight'] < 0.001:
        treenode['edge_data']['weight'] = 1.0     
    nodecount += 1
    # look at the current tree node and see if there are children or not
    if 'children' in treenode.keys():
        #case for a hierarchy node, traverse to all the children
        for node in treenode['children']:
            assignEdgeWeightsIfMissing(node)

